pragma solidity ^0.4.24;
import "../SharedContracts/VuupleToken.sol";
// import "../SharedContracts/DateTimeParser.sol";

// this contract hold the token and at the end of the month , if the lender get the required reputational points , the ownership of the token transfered to this lender  if not , these token is burnt
contract LenderEscrow  {
    enum EscrowStatus{Active,Completed ,Closed}
    VuupleToken private vuupleToken;
    EscrowStatus public escrowStatus ;
    uint256 public closeTime;
    address public lenderAccountContract;
    address public lender;
    uint256 public tokenAmount;
    constructor(VuupleToken _vuupleToken,
        uint256 _closeTime,
        uint256 _tokenAmount,
        address _lender){
        vuupleToken =_vuupleToken;
        closeTime =_closeTime;
        tokenAmount =_tokenAmount;
        lender =_lender;
        escrowStatus = EscrowStatus.Active;
        lenderAccountContract = msg.sender;
    }
    modifier isLenderAccountContract(){
        require(lenderAccountContract==msg.sender);
        _;
    }
    modifier subscriptionEnded() {
        require( now >= closeTime);
        _;
    }
    modifier escrowActive() {
        require( escrowStatus == EscrowStatus.Active);
        _;
    }


    function isActive( ) public view returns(bool) {
        return escrowStatus == EscrowStatus.Active ? true :false;
    }
    function withdraw() external isLenderAccountContract subscriptionEnded escrowActive returns (bool) {
        _transferToken(tokenAmount);
        // tokenAmount=0;
        escrowStatus = EscrowStatus.Completed;
        return true;
    }
    function closeEscrow() external isLenderAccountContract  escrowActive returns (bool)  {
        _burn(tokenAmount);
        // tokenAmount=0;
        escrowStatus = EscrowStatus.Closed;
        return true;
    }
    function _burn(uint256 _tokenAmount) internal returns (bool) {
        vuupleToken.burn(_tokenAmount);
        return true;
    }
    function _transferToken(uint256 _tokenAmount) internal returns (bool) {
        vuupleToken.transfer(lender,_tokenAmount);
        return true;
    }

}
