pragma solidity ^0.4.24;
import "../SharedContracts/VuupleToken.sol";
import "../SharedContracts/VuupleAdmins.sol";

// import "../SharedContracts/DateTimeParser.sol";

// this contract hold the token and at the end of the month , if the renter get the required reputational points , the ownership of the token transfered to this renter  if not , these token is burnt
contract RenterEscrow  {
    enum EscrowStatus{Active,Completed ,Closed}
    VuupleToken public vuupleToken;
    EscrowStatus public escrowStatus ;
    uint256 public closeTime;
    address public renterAccountContract;
    address public renter;
    uint256 public tokenAmount;
    VuupleAdmins public vuupleAdmin;

    constructor(VuupleAdmins _adminAddress,VuupleToken _vuupleToken,
        uint256 _closeTime,
        uint256 _tokenAmount,
        address _renter){
        vuupleAdmin = _adminAddress;
        vuupleToken =_vuupleToken;
        closeTime =_closeTime;
        tokenAmount =_tokenAmount;
        renter =_renter;
        escrowStatus = EscrowStatus.Active;
        renterAccountContract = msg.sender;
    }
    modifier onlyVuupleAdmin() {
        require(vuupleAdmin.isVuupleAdmin(msg.sender));
        _;
    }
    modifier isrenterAccountContract(){
        require(renterAccountContract==msg.sender);
        _;
    }
    modifier subscriptionEnded() {
        require( now >= closeTime);
        _;
    }
    modifier escrowActive() {
        require( escrowStatus == EscrowStatus.Active);
        _;
    }


    function isActive( ) public view returns(bool) {
        return escrowStatus == EscrowStatus.Active ? true :false;
    }
    // burn token to return back to the token contract
    function withdraw()  onlyVuupleAdmin subscriptionEnded escrowActive returns (bool) {
        _burn(tokenAmount);
        // tokenAmount=0;
        escrowStatus = EscrowStatus.Completed;
        return true;
    }
    function closeEscrow() external isrenterAccountContract  escrowActive returns (bool)  {
        _burn(tokenAmount);
        // tokenAmount=0;
        escrowStatus = EscrowStatus.Closed;
        return true;
    }
    function _burn(uint256 _tokenAmount) internal returns (bool) {
        vuupleToken.burn(_tokenAmount);
        return true;
    }
    // function _transferToken(uint256 _tokenAmount) internal returns (bool) {
    //     vuupleToken.transfer(renter,_tokenAmount);
    //     return true;
    // }

}
